import React, {useState} from 'react';
import './App.css';
import Intro from "./Intro/Intro";
import Work from "./components/work";
import Footer from "./components/footer/footer";
import Navbar from "./Nav";
import {ThemeProvider, themes} from "./ThemeProvider";
import ThemeTogglerButton from "./themed-button";
function Content(){
    return(
        <div>
            <ThemeTogglerButton/>
        </div>
    )
}
function App() {
    const [state, setState] = useState({ThemeContext: ThemeProvider})
    const toggleTheme = (state) => {

        setState({theme: state.theme === themes.dark ? themes.light : themes.dark})

    }
    return (
        <ThemeProvider.Provider value={state}>
            <Content/>
            <div className="App">

                {/*<Navbar/>*/}
                <Intro/>
                {/*<Work/>*/}
                <Footer/>
            </div>
        </ThemeProvider.Provider>
    );
}

export default App;
